import React, { useState } from 'react';
import PokemonCard from './components/PokemonCard'; // Import PokemonCard component
import SortMenu from './components/SortMenu'; // Import SortMenu component
import './App.css';

function App() {
  const [isSingleColumn, setIsSingleColumn] = useState(true); // Default to single column

  // Dummy list of Pokémon
  const pokemonList = [
    { id: 1001, name: 'Bulbasaur', type: 'Grass', image: 'pokemon1.png' },
    { id: 1002, name: 'Ivysaur', type: 'Grass', image: 'pokemon2.png' },
    { id: 1003, name: 'Venusaur', type: 'Grass', image: 'pokemon3.png' },
    { id: 1004, name: 'Charmander', type: 'Fire', image: 'pokemon4.png' },
    { id: 1005, name: 'Charmeleon', type: 'Fire', image: 'pokemon5.png' },
    { id: 1006, name: 'Charizard', type: 'Fire', image: 'pokemon6.png' },
    { id: 1007, name: 'Squirtle', type: 'Water', image: 'pokemon7.png' },
    { id: 1008, name: 'Wartortle', type: 'Water', image: 'pokemon8.png' },
    { id: 1009, name: 'Blastoise', type: 'Water', image: 'pokemon9.png' }
  ];

  return (
    <div className="App">
      <h1>Pokémon List</h1>
      <SortMenu setIsSingleColumn={setIsSingleColumn} /> {/* Pass function to SortMenu to control grid layout */}
      <div className={`pokemon-card-list ${isSingleColumn ? 'single-column' : 'two-columns'}`}>
        {pokemonList.map((pokemon) => (
          <PokemonCard key={pokemon.id} pokemon={pokemon} />
        ))}
      </div>
    </div>
  );
}

export default App;
