import React, { useState } from 'react';
import './SortMenu.css'; // Mengimpor CSS untuk styling

function SortMenu() {
  const [sortOption, setSortOption] = useState(''); // State untuk menyimpan pilihan sort

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  return (
    <div className="sort-menu">
      {/* Dropdown untuk "Sort by" */}
      <div className="dropdown">
        <select
          value={sortOption}
          onChange={handleSortChange}
          className="sort-dropdown"
        >
          <option value="">Sort by</option>
          <option value="name">Name</option>
          <option value="date">Date</option>
          <option value="popularity">Popularity</option>
        </select>
      </div>

      {/* Ikon grid dan list */}
      <div className="icons">
        <div className="icon grid-left">
          <img src="/OnePokemon/grid-1.png" alt="" />
        </div>
        <div className="icon grid-right">
          <img src="/OnePokemon/grid-2.png" alt="" />
        </div>
      </div>
    </div>
  );
}

export default SortMenu;
