import React from 'react';
import './PokemonCard.css'; // Mengimpor CSS untuk styling

function PokemonCard() {
  // Data untuk Pokémon
  const pokemonType = 'Grass';
  const pokemonName = 'Bulbasaur';
  const pokemonId = 1001;

  return (
    <div className="pokemon-card">
      <div className="pokemon-card-header">
        <span className="pokemon-card-type">{pokemonType}</span>
        <span className="pokemon-card-id">#{pokemonId}</span>
      </div>
      <div className="pokemon-card-image">
        <img src="/OnePokemon/bulbasaur.png" alt={pokemonName} className="pokemon-card-img" />
      </div>
      <div className="pokemon-card-name">
        <p>{pokemonName} in one line</p>
      </div>
    </div>
  );
}

export default PokemonCard;
